import './App.css';
import SideBar from './Sidebar';
import NavBar from './Navbar';
import TradingViewWidget, { Themes } from 'react-tradingview-widget';
import { useState, useEffect } from "react";

const tableData = [
  {market: "BTCUSDC", time: 'Sun, 31 Jul', price: "43,935.23", amount:'0.00351', side:'BUY', status:'CANCEL'},
  {market: "BTCUSDC", time: 'Sun, 31 Jul', price: "43,935.23", amount:'0.00351', side:'BUY', status:'CANCEL'},
  {market: "BTCUSDC", time: 'Sun, 31 Jul', price: "43,935.23", amount:'0.00351', side:'BUY', status:'CANCEL'},
  {market: "BTCUSDC", time: 'Sun, 31 Jul', price: "43,935.23", amount:'0.00351', side:'BUY', status:'CANCEL'}, ,

]

function App() {
  const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState(null);

 const [isOpen, setIsOpen] = useState(false);

 useEffect(() => {
  const getData = async () => {
    try {
      const response = await fetch(
        `wss://api-betatestnet.brine.finance/liveorderbookwire`
      );
      if (!response.ok) {
        throw new Error(
          `This is an HTTP error: The status is ${response.status}`
        );
      }
      let actualData = await response.json();
      setData(actualData);
      setError(null);
    } catch(err) {
      setError(err.message);
      setData(null);
    } finally {
      setLoading(false);
    }  
  }
  getData()
}, [])
  return (
   <div>
  <SideBar />
  <NavBar />
  <div style={{ paddingLeft:100, display: "flex"}}>
    <div style={{width: 700, height:'100%'}}>
      <TradingViewWidget
        symbol="NASDAQ:AAPL"
        theme={Themes.DARK}
        width="700"
        height="400"
        />
      <div>
        <div style={{display:'flex', height:70}}>
          <div style={{borderRadius: 10, color:'#E0B0FF', backgroundColor:'#191B21', marginLeft:50, height:25, width:100, marginTop:20, textAlign:'center', fontSize:15}}>All Orders</div>
        </div>
      <table style={{border: '1px solid gray', width: '100%', height: 300}}>
        <tr>
          <th>MARKET</th>
          <th>TIME</th>
          <th>PRICE</th>
          <th>AMOUNT</th>
          <th>SIDE</th>
          <th>STATUS</th>
        </tr>
        {tableData.map((val, key) => {
          return (
            <tr key={key}>
              <td>{val.market}</td>
              <td>{val.time}</td>
              <td>{val.price}</td>
              <td>{val.amount}</td>
              <td style={{color:'green'}}>{val.side}</td>
              <td style={{color:'red'}}>{val.status}</td>
            </tr>
          )
        })}
      </table>
      </div>
    </div>
    <div style={{width: '25%', height:'100%'}}>
      <div >
      <table>
        <tr>
          <th>Amount (BTC)</th>
          <th>Price (USDT)</th>
        </tr>

      </table>
        </div>
    </div>
    <div style={{width: '25%', height:'100%', backgroundColor:'#333333'}}>Column 3</div>
  </div>

   </div>
  );
}

export default App;
